import axios from "axios";
import { query, mode, dateObj } from "./types";

async function product_search(
  techspecs_base_url: string,
  query: query,
  techspecs_api_key: string,
  mode: mode = "pretty"
) {
  const url = `${techspecs_base_url}/v4/product/search?query=${query.keyword.replace(
    " ",
    "%20"
  )}`;
  const headers = {
    Accept: "application/json",
    Authorization: `Bearer ${techspecs_api_key}`,
    "Content-Type": "application/json",
  };
  const payload = { category: query.category };
  const req = await axios.post(url, payload, {
    headers: headers,
  });

  if (mode === "raw") {
    return req.data.data;
  } else if (mode === "pretty") {
    try {
      return req.data.data;
    } catch (err) {
      return req.data.data;
    }
  } else {
    return "Invalid Mode";
  }
}

async function product_detail(
  techspecs_base_url: string,
  techspecs_product_id: string,
  techspecs_api_key: string,
  mode = "pretty"
) {
  const url = `${techspecs_base_url}/v4/product/detail/${techspecs_product_id}`;
  const header = {
    Accept: "application/json",    
    Authorization: `Bearer ${techspecs_api_key}`,
    "Accept-Encoding": "gzip, deflate",
  };
  const req = await axios.get(url, {
    headers: header,
  });
  if (mode === "raw") {
    return req.data.data;
  } else if (mode === "pretty") {
    try {
      const mod_list = req.data.data.product;
      const modified_data = [];
      for (const m of mod_list) {
        // destruct object upto 2 levels
        const mod_object: Object = {};

        for (const [key, value] of Object.entries(m)) {
          if (typeof value === "object") {
            for (const [key2, value2] of Object.entries(value as object)) {
              // @ts-ignore
              mod_object[key2] = value2;
            }
          } else {
            // @ts-ignore
            mod_object[key] = value;
          }
        }
        modified_data.push(mod_object);
      }
      return modified_data;
    } catch (err) {
      return req.data.data;
    }
  } else {
    return "Invalid Mode";
  }
}

