async function get_all_products(
  techspecs_base_url: string,
  brand: string[],
  category: string[],
  date: dateObj,
  page: number,
  techspecs_api_key: string,
  mode = "pretty"
) {
  const url = `${techspecs_base_url}/v4/all/products?page=${page}`;
  const header = {
    Accept: "application/json",
    Authorization: `Bearer ${techspecs_api_key}`,
    "Content-Type": "application/json",
  };
  const payload = {
    brand: brand,
    category: category,
    date: date.from,
    to: date.to,
  };

  const req = await axios.post(url, payload, {
    headers: header,
  });
  if (mode === "raw") {
    return req.data.data;
  } else if (mode === "pretty") {
    try {
      return req.data.data.product;
    } catch (err) {
      return req.data.data;
    }
  } else {
    return "Invalid Mode";
  }
}

export const techspecs = {
  product_search,
  product_detail,
  get_all_brands,
  get_all_categories,
  get_all_products,
};
