import { techspecs } from "techspecs";

(async () => {
  const techspecs_api_key = "techspecs_api_key";
  const techspecs_base_url = "https://api.techspecs.io";
  const page = 0;
  const brand = ["Apple"];
  const category = ["Smartphones"];
  const date = {
    from: "2010-01-01",
    to: "2022-03-15",
  };

  const response = await techspecs.get_all_products(
    techspecs_base_url,
    brand,
    category,
    date,
    page,
    techspecs_api_key,
    "pretty"
  );

  console.log(response);
})();
